<template>    <v-content>        <v-container fluid fill-height>            <v-alert                    :value="success"                    type="success"                    transition="scale-transition"            >                更新しました。            </v-alert>            <v-alert                    :value="!valid"                    type="warning"                    transition="scale-transition"            >                入力に誤りがあります。            </v-alert>            <v-layout                    justify-center                    text-xs-center                    wrap                    row            >                <v-form ref="form" lazy-validation>                    <v-flex xs-12 class="mb-5">                        <div class="title">                            プロフィール編集                        </div>                    </v-flex>                    <v-flex xs-12>                        <v-text-field                                v-model="name"                                :rules="[rules.required]"                                label="Name"                                placeholder="Inpupt Your Name"                                required                        ></v-text-field>                        <v-text-field                                v-model="email"                                :rules="[rules.required, rules.email]"                                label="E-mail"                                placeholder="xxxx@example.com"                                required>                        </v-text-field>                        <v-btn                                @click="save">                            submit                        </v-btn>                    </v-flex>                </v-form>            </v-layout>        </v-container>    </v-content></template><script lang="ts">
import {Component, Vue} from 'vue-property-decorator';  import {VAlert, VBtn, VCheckbox, VContainer, VFlex, VForm, VLayout, VSelect, VTextField} from 'vuetify/lib';  import catalog from '@/api/Validations';  @Component({    components: {      VForm, VLayout, VContainer, VCheckbox, VBtn, VSelect, VTextField, VAlert, VFlex,    },  })  export default class Profile extends Vue {    public valid: boolean = true;    public success: boolean = false;    public name: string = '';    public email: string = '';    public select?: string | null;    public rules: object = catalog;    public save() {      // noinspection TypeScriptUnresolvedFunction      if (!(this.$refs.form as any).validate()) {        this.valid = false;        setTimeout(() => {          this.valid = true;        }, 1000);        return '';      }      localStorage.setItem('profile.name', this.name);      localStorage.setItem('profile.email', this.email);      this.success = true;      setTimeout(() => {        this.success = false;      }, 1000);    }  }
</script><style scoped></style>